import React, { useState, useEffect } from 'react';
import uccplogo from '../assets/uccplogo.png';
import schlogo from '../assets/schlogo.png';

function Login() {
  const [isSmallScreen, setIsSmallScreen] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsSmallScreen(window.innerWidth < 640);
    };

    // Initial check and add event listener
    handleResize();
    window.addEventListener('resize', handleResize);

    // Clean up the event listener on component unmount
    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <div className=''>
      <div className='flex bg-custom-green h-20 items-center'>
        <div className='ml-2 mr-2'>
          <img src={uccplogo} alt="" className='h-16 w-16 lg:h-20 lg:w-20 transition-all duration-300' />
        </div>
        <div className=''>
          <img src={schlogo} alt="" className='h-20 w-20 lg:h-24 lg:w-24 transition-all duration-300' />
        </div>
        <div className='flex ml-4 grid-cols-3'>
          {/* Conditional rendering based on screen size */}
          <p className={`text-white text-lg lg:text-2xl font-custom-font ${isSmallScreen ? 'text-center text-3xl' : ''} transition-all duration-300`}>
            {isSmallScreen ? 'COCS-UCCP' : 'CAGAYAN DE ORO CHRISTIAN SCHOOL - UCCP'}
          </p>
        </div>
        {/* Navigation Bar */}
        {/* Add your navigation bar here */}
      </div>
    </div>
  );
}

export default Login;
