import React from 'react';
import Appbar from '../surface/Appbar';
import uccplogo from '../assets/uccplogo.png';
import schlogo from '../assets/schlogo.png';

function Dashboard() {
  return (  
    <div className=''>
      <Appbar/>
      <section className='h-screen grid gap-8 md:grid-cols-2 absolute'>
        <div className='flex-1 flex flex-col items-center justify-normal md:items-center md:justify-center md:mt-24'>
          <img src={schlogo} alt="" className='h-22 w-22' />
        </div>
        <div className='flex-1 px-4 flex flex-col text-center justify-center mt-0 mx-2 md:text-start md:justify-center md:mt-24'>
          <p className=' text-2xl sm:text-2xl md:text-3xl font-bold mb-2'>Welcome to</p>
          <h1 className='text-2xl sm:text-3xl md:text-6xl font-bold mb-2' style={{ color: '#079440' }}>Cagayan de Oro Christian School - UCCP <h1 style={{ color: '#F2B569' }}>E-Portal</h1></h1>
          <p className='text-2xl sm:text-2xl md:text-2xl font-semibold mb-2 italic'>Give your child an education that is nurtured by Christ-centered ideals.</p>
        </div>
      </section>
    </div>
  );
}

export default Dashboard;
