import React, { useState, useEffect, useRef } from 'react';
import Appbar from '../surface/Appbar';
import uccplogo from '../assets/uccplogo.png';
import schlogo from '../assets/schlogo.png';

function Dashboard() {
  const [currentImage, setCurrentImage] = useState(0);
  const imageSources = [schlogo, uccplogo]; // Add more image sources as needed

  const imageRef = useRef(null);

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentImage((prevImage) => (prevImage + 1) % imageSources.length);
    }, 3000); // Adjust the interval time as needed

    return () => clearInterval(intervalId); // Cleanup interval on component unmount
  }, [imageSources.length]);

  const imageStyles = {
    image1: { height: '30rem', width: '30rem' }, // Adjust the height and width for the first image
    image2: { height: '30rem', width: '30rem' }, // Adjust the height and width for the second image
    // Add more styles for additional images as needed
  };

  const currentImageStyle = imageStyles[`image${currentImage + 1}`];

  useEffect(() => {
    const options = {
      root: null,
      rootMargin: '0px',
      threshold: 0.5, // Adjust the threshold as needed
    };

    const observer = new IntersectionObserver((entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          // The section is in view, you can perform actions here
          console.log('Section is in view');
        }
      });
    }, options);

    // Start observing the imageRef
    if (imageRef.current) {
      observer.observe(imageRef.current);
    }

    return () => {
      // Cleanup the observer on component unmount
      if (imageRef.current) {
        observer.unobserve(imageRef.current);
      }
    };
  }, []);

  return (
    <div className='flex flex-col h-screen'>
      <Appbar />
      <section className='h-screen grid gap-8 md:grid-cols-2'>
        <div className='flex-1 flex flex-col items-center justify-center md:items-center md:justify-center'>
          <img
            src={imageSources[currentImage]}
            alt=""
            style={currentImageStyle} // Apply the specific style for the current image
            ref={imageRef} // Set the ref for Intersection Observer
          />
        </div>
        <div className='flex-1 px-4 flex flex-col text-center justify-center mt-0 mx-2 lg:text-start lg:justify-center'>
          <p className='text-2xl sm:text-2xl md:text-3xl font-bold mb-2'>Welcome to</p>
          <h1 className='text-2xl sm:text-3xl md:text-6xl font-bold mb-2' style={{ color: '#079440' }}>
            Cagayan de Oro Christian School - UCCP <span style={{ color: '#F2B569' }}>E-Portal</span>
          </h1>
          <p className='text-2xl sm:text-2xl md:text-2xl font-semibold mb-2 italic'>
            "Give your child an education that is nurtured by Christ-centered ideals.
          </p>
        </div>
      </section>
      {/* About Section */}
      <section className='h-screen grid gap-8 md:grid-cols-2'>
        <div className='flex-1 px-4 flex flex-col text-center justify-center mt-0 mx-2 lg:text-start lg:justify-center'>
          <h2 className='text-3xl font-bold mb-4'>About Us</h2>
          <p className='text-xl mb-4'>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
          </p>
          {/* Add more content as needed */}
        </div>
      </section>
    </div>
  );
}

export default Dashboard;
