import React, { useState, useEffect } from 'react';
import { Link } from 'react-scroll';
import Appbar from '../surface/Appbar';
import uccplogo from '../assets/uccplogo.png';
import schlogo from '../assets/schlogo.png';

function Dashboard() {
  const [currentImage, setCurrentImage] = useState(0);
  const imageSources = [schlogo, uccplogo];

  useEffect(() => {
    const intervalId = setInterval(() => {
      setCurrentImage((prevImage) => (prevImage + 1) % imageSources.length);
    }, 3000);

    return () => clearInterval(intervalId);
  }, [imageSources.length]);

  const imageStyles = {
    image1: { height: '30rem', width: '30rem' },
    image2: { height: '30rem', width: '30rem' },
  };

  const currentImageStyle = imageStyles[`image${currentImage + 1}`];

  return (
    <div className='flex flex-col'>
      <Appbar />
      <Link to='about' smooth={true} duration={500} className='scroll-link'>
        Scroll to About Us
      </Link>
      <section className='full-height' id='section1'>
        <div className='flex-1 flex flex-col items-center justify-center'>
          <img src={imageSources[currentImage]} alt="" style={currentImageStyle} />
        </div>
      </section>
      <section className='full-height' id='about'>
        <div className='flex-1 px-4 flex flex-col text-center justify-center mt-0 mx-2 lg:text-start lg:justify-center'>
          <h2 className='text-3xl font-bold mb-4'>About Us</h2>
          <p className='text-xl mb-4'>
            Lorem ipsum dolor sit amet, consectetur adipiscing elit. Sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
          </p>
          {/* Add more content as needed */}
        </div>
      </section>
    </div>
  );
}

export default Dashboard;
